INSERT INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`, `emote_id1`) VALUES
(120000, 'How dare you, cursed heretics! The rabbit\'s power shall be ours!', 'How dare you, cursed heretics! The rabbit\'s power shall be ours!', 0, 0),
(120001, 'Good job on finding our little white friend, but we are not done yet. Now we have to figure out a way to get the bunny to safety.$B$BI\'m going to open a portal to Dalaran, but it will take a while to complete the spell. I need you, heroes, to protect the cathedral until the portal has been opened.', 'Good job on finding our little white friend, but we are not done yet. Now we have to figure out a way to get the bunny to safety.$B$BI\'m going to open a portal to Dalaran, but it will take a while to complete the spell. I need you, heroes, to protect the cathedral until the portal has been opened.', 0, 2),
(120002, 'We are ready. Start the ritual.', 'We are ready. Start the ritual.', 0, 0),
(120003, 'I am senior summoner Yolkman and I\'m going to help you defend the cathedral. After many years of study, I have devised special summoning scrolls that can harness the power of the Eeaster Eggs!$B$BUse them to call in reinforcements from the Kirin Tor. Stopping the Scarlet Crusade\'s counter-attack alone will not be easy.', 'I am senior summoner Yolkman and I\'m going to help you defend the cathedral. After many years of study, I have devised special summoning scrolls that can harness the power of the Eeaster Eggs!$B$BUse them to call in reinforcements from the Kirin Tor. Stopping the Scarlet Crusade\'s counter-attack alone will not be easy.', 0, 0),
(120004, 'Let me have a look at these scrolls.', 'Let me have a look at these scrolls.', 0, 0),
(120005, 'Here they come! Defend the keep at all costs!', 'Here they come! Defend the keep at all costs!', 6, 0),
(120006, 'What is the meaning of this!? I\'ll make you pay for your insolence!', 'What is the meaning of this!? I\'ll make you pay for your insolence!', 0, 0),
(120007, 'Arming egg-splosive payload. All systems operational!', 'Arming egg-splosive payload. All systems operational!', 0, 0),
(120008, 'Alert! Alert! Intruders detected! Engaging omelette directive.', 'Alert! Alert! Intruders detected! Engaging omelette directive.', 6, 0),
(120009, 'That was just a scouting force. More fanatics are coming!', 'That was just a scouting force. More fanatics are coming!', 0, 0),
(120010, 'I can hear more enemies coming. Get ready!', 'I can hear more enemies coming. Get ready!', 0, 0),
(120011, 'More of their reinforcements are arriving. Protect the rabbit at all costs!', 'More of their reinforcements are arriving. Protect the rabbit at all costs!', 0, 0),
(120012, 'Your time is over, intruders. May the light have mercy on you, as I surely won\'t.', 'Your time is over, intruders. May the light have mercy on you, as I surely won\'t.', 6, 0),
(120013, 'The end is nigh, heathens. I have come to cleanse these grounds of infidels. Use these last moments of life to reflect on your mistakes and repent.', 'The end is nigh, heathens. I have come to cleanse these grounds of infidels. Use these last moments of life to reflect on your mistakes and repent.', 6, 0),
(120014, 'The portal is almost opened! Give me just three more minutes, and we can get out of this cursed place.', 'The portal is almost opened! Give me just three more minutes, and we can get out of this cursed place.', 0, 0),
(120015, 'Behold, the true meaning of power. Not to take life, but to give it back!', 'Behold, the true meaning of power. Not to take life, but to give it back!', 6, 0),
(120016, 'Fallen champions of Lordaeron, I grant you one last breath!', 'Fallen champions of Lordaeron, I grant you one last breath!', 6, 0),
(120017, 'All is lost, the easter bunny is dead. You fought bravely, champions, but it was not enough.', 'All is lost, the easter bunny is dead. You fought bravely, champions, but it was not enough.', 6, 0),
(120018, 'You\'ve done it! The easter bunny has escaped through the portal. Azeroth is saved!', 'You\'ve done it! The easter bunny has escaped through the portal. Azeroth is saved!', 6, 0),
(120019, 'Hello, $c. I\'ve been sent here by the Kirin Tor to recruit heroes willing to help us rescue Azeroth\'s most beloved bunny from the hands of the Scarlet Crusade.$B$BIf you think you\'re up to the task, I can transport you to the monastery where the bunny is being held.', 'Hello, $c. I\'ve been sent here by the Kirin Tor to recruit heroes willing to help us rescue Azeroth\'s most beloved bunny from the hands of the Scarlet Crusade.$B$BIf you think you\'re up to the task, I can transport you to the monastery where the bunny is being held.', 0, 1);
