INSERT INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`) VALUES (110000, 'AAAAAAAAAAA! ENOUGH YOU PITIFUL WRETCHES!', 'AAAAAAAAAAA! STOP IT YOU PITIFUL WRETCHES!', 1);
INSERT INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`) VALUES (110001, 'No more play. You want to see my true form? So be it.', 'No more play. You want to see my true form? So be it.', 1);
INSERT INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`) VALUES (110002, 'All aboard the pain train! Destination: The Afterlife.', 'All aboard the pain train! Destination: The Afterlife,', 1);
INSERT INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`) VALUES (110003, 'My curse is broken!', 'My curse is broken!', 0);
INSERT INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`) VALUES (110004, 'Thank you for freeing me, heroes. I can find my way home from here.', 'Thank you for freeing me heroes. I can find my way home from here.', 0);
INSERT INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`) VALUES (110005, 'Pathetic. This dimension is a waste of my time.', 'Pathetic. This dimension is a waste of my time.', 1);
INSERT INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`) VALUES (110006, 'Careful, look over there. $B$BDo you see that raptor? It may look perfectly ordinary to you, but it\'s not! I have observed it for several hours, and I am certain that this is the Grinch in disguise. $B$BIf I am right, it should try to run away when you throw a snowball at it. Or if I am wrong and that\'s a real raptor, it\'s going to bite your face off, but you look more than capable to handle that!', 'Careful, look over there.$B Do you see that raptor? It may look perfectly ordinary to you, but it\'s not! I have observed it for several hours, and I am certain that this is the Grinch in disguise. If I am right, it should try to run away when you throw a snowball at it. Or if I am wrong and that\'s a real raptor, it\'s going to bite your face off, but you look more than capable to handle that!', 0);
INSERT INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`) VALUES (110007, 'Father Winter has prepared some toys that will help you in your quest!', 'Father Winter has prepared some toys that will help you in your quest!', 0);
INSERT INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`) VALUES (110008, 'Hey, over here!', 'Hey, over here!', 0);
INSERT INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`) VALUES (110009, 'Free at last!', 'Free at last!', 0);
INSERT INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`) VALUES (110010, 'Blessed ancestors, I am freed!', 'Blessed ancestors, I am freed!', 0);
INSERT INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`) VALUES (110011, 'Finally my chains are broken.', 'Finally my chains are broken.', 0);
INSERT INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`) VALUES (110012, 'Don\'t let my death be in vain. Strike him now!', 'Don\'t let my death be in vain. Strike him now!', 0);
INSERT INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`) VALUES (110013, 'Revenge me...', 'Revenge me...', 0);
INSERT INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`) VALUES (110014, '%s summons the souls of his victims.', '%s summons the souls of his victims.', 2);
INSERT INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`) VALUES (110015, '%s is about to implode in a dark blast of energy!', '%s is about to implode in a dark blast of energy!', 2);
INSERT INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`) VALUES (110016, 'Top quality snow, get it before it melts!', 'Top quality snow, get it before it melts!', 0);
INSERT INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`) VALUES (110017, 'Planning on fighting the Grinch? You better buy some snowballs first!', 'Planning on fighting the Grinch? You better buy some snowballs first!', 0);
INSERT INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`) VALUES (110018, 'Noooooo! You might have won this battle, little mortals, but another will take my place next winter...', 'Noooooo! You might have won this battle, little mortals, but another will take my place next winter...', 1);
INSERT INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`) VALUES (110019, 'He is hiding in this very chamber! Disguised as some critter no doubt. You will only get one chance to fight him, so make it worth it.', 'He is hiding in this very chamber! Disguised as some critter no doubt. You will only get one chance to fight him, so make it worth it.', 0);
INSERT INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`) VALUES (110020, '%s stares at you in a very suspicous manner.', '%s stares at you in a very suspicous manner.', 2);
INSERT INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`) VALUES (110021, 'You have done it! Greatfather Winter will surely give you a very special present now!', 'You have done it! Greatfather Winter will surely give you a very special present now!', 0);
INSERT INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`) VALUES (110022, 'On no, the Grinch got away! $B$BI hope you can get him next time. The Winter Veil celebrations are in danger with such a monster on the loose!', 'On no, the Grinch got away! $B$BI hope you can get him next time. The Winter Veil celebrations are in danger with such a monster on the loose!', 0);
INSERT INTO `broadcast_text` (`entry`, `male_text`, `female_text`, `chat_type`) VALUES (110023, 'He opened a dimensional portal and escaped into the Nether! I wouldn\'t try to follow him if I were you.', 'He opened a dimensional portal and escaped into the Nether! I wouldn\'t try to follow him if I were you.', 0);
