UPDATE `quest_template` SET `OfferRewardEmote1`=4, `OfferRewardText`='The Malt is brewed, the Boars are dead$bAnd before all is done and anything said$bWe will have to fight for first dibs$bOn these savory Beer Basted Boar Ribs!' WHERE `entry`=384;
