UPDATE `quest_template` SET `OfferRewardText`='Shiver me timbers!  Ye found the Cap\'n\'s grog!$b$bI owe ye big time, friend.$b$bSeems like a storm is a brewin\' outside.  Maybe I\'ll just keep warm with one o\' these here bottles while we wait for it to pass....' WHERE `entry`=580;
